import {
  Streamlit,
  withStreamlitConnection,
  ComponentProps
} from "streamlit-component-lib"
import React, { useEffect, useState, useRef } from "react"
import { ChakraProvider, Input, Box, HStack, VStack, Center, Button, Text, Flex } from '@chakra-ui/react'
import { extendTheme } from '@chakra-ui/react';
import useImage from 'use-image';
import BBoxCanvas from "./BBoxCanvas";

// ÌÖåÎßà ÏÑ§Ï†ï - Í∞ïÏ†ú ÎùºÏù¥Ìä∏ Î™®Îìú
const theme = extendTheme({
  config: {
    initialColorMode: 'light',
    useSystemColorMode: false,
  },
});

export interface PythonArgs {
  image_url: string,
  image_size: number[],
  bbox_info: any[],
  color_map: any,
  line_width: number,
  use_space: boolean,
  ocr_suggestions: string[]
}

interface Rectangle {
  x: number;
  y: number;
  width: number;
  height: number;
  label: string;
  stroke: string;
  id: string;
}

const Detection = ({ args }: ComponentProps) => {
  const {
    image_url,
    image_size,
    bbox_info,
    color_map,
    line_width,
    use_space,
    ocr_suggestions

  }: PythonArgs = args

  const params = new URLSearchParams(window.location.search);
  const baseUrl = params.get('streamlitUrl')
  const [image] = useImage(baseUrl + image_url)

  const [rectangles, setRectangles] = React.useState<Rectangle[]>(
    bbox_info.map((bb, i) => ({
      x: bb.bbox[0],
      y: bb.bbox[1],
      width: bb.bbox[2],
      height: bb.bbox[3],
      label: bb.label,
      stroke: "#39FF14",  // color_map[bb.label],
      id: 'bbox-' + i
    }))
  );

  const [selectedId, setSelectedId] = React.useState<string | null>(null);
  const [label, setLabel] = useState(""); 
  const [mode, setMode] = React.useState<string>('Draw');
  const [isLabelEditMode, setIsLabelEditMode] = useState(false);
  const [scale, setScale] = useState(1.0);
  const [saveNotification, setSaveNotification] = useState<boolean>(false);
  const [showLabels, setShowLabels] = useState(false);
  const [suggestedLabels, setSuggestedLabels] = useState<string[]>([]);
  const [isLoadingLabels, setIsLoadingLabels] = useState<boolean>(false);
  const [showSuggestions, setShowSuggestions] = useState<boolean>(false);


  useEffect(() => {
    if (ocr_suggestions && ocr_suggestions.length > 0) {
      console.log("Received OCR suggestions from props:", ocr_suggestions);
      setSuggestedLabels(ocr_suggestions);
      setIsLoadingLabels(false);
      setShowSuggestions(true);
    }
  }, [ocr_suggestions]);

  // Streamlit Ïª¥Ìè¨ÎÑåÌä∏ ÌÜµÏã† Í∞úÏÑ†
  const sendStateToStreamlit = (currentMode: string, boxesData: Rectangle[], currentScale: number, selectedBoxId: string | null = null) => {
    try {
      // ÌïÑÏöîÌïú Îç∞Ïù¥ÌÑ∞Îßå Ï†ÑÏÜ°ÌïòÎèÑÎ°ù ÏµúÏ†ÅÌôî
      const currentBboxValue = boxesData.map((rect) => ({
        bbox: [rect.x, rect.y, rect.width, rect.height],
        label: rect.label || "", // Îπà ÎùºÎ≤® ÏïàÏ†ÑÌïòÍ≤å Ï≤òÎ¶¨
      }));

      // OCR ÏöîÏ≤≠ ÏãúÏóêÎßå full data Ï†ÑÏÜ°, ÏïÑÎãê ÎïåÎäî ÏµúÏÜå Îç∞Ïù¥ÌÑ∞Îßå
      if (selectedBoxId !== null) {
        console.log("DEBUG: OCR ÏöîÏ≤≠ Ï†ÑÏÜ°", selectedBoxId);

        // ÏÑ†ÌÉùÎêú Î∞ïÏä§Í∞Ä Ï°¥Ïû¨ÌïòÎäîÏßÄ ÌôïÏù∏
        const selectedBox = boxesData.find(box => box.id === selectedBoxId);
        if (!selectedBox) {
          console.error("ÏÑ†ÌÉùÎêú Î∞ïÏä§Î•º Ï∞æÏùÑ Ïàò ÏóÜÏùå");
          return;
        }

        Streamlit.setComponentValue({
          mode: currentMode,
          bboxes: currentBboxValue,
          scale: currentScale,
          save_requested: false,
          request_ocr: true,
          selected_box_id: selectedBoxId,
          selected_box_coords: [selectedBox.x, selectedBox.y, selectedBox.width, selectedBox.height]
        });
      } else {
        // ÏùºÎ∞ò ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ÏóêÏÑúÎäî OCR Í¥ÄÎ†® ÌîåÎûòÍ∑∏ Ï†úÏô∏
        Streamlit.setComponentValue({
          mode: currentMode,
          bboxes: currentBboxValue,
          scale: currentScale,
          save_requested: false
        });
      }
    } catch (error) {
      console.error("ÏÉÅÌÉú Ï†ÑÏÜ° Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    }
  };

const pendingOCRRequestRef = useRef(false);

const sendOCRRequest = (selectedId: string, mode: string, rectangles: Rectangle[], scale: number) => {
  if (pendingOCRRequestRef.current) {
    console.log("Ïù¥ÎØ∏ Ï≤òÎ¶¨ Ï§ëÏù∏ OCR ÏöîÏ≤≠Ïù¥ ÏûàÏäµÎãàÎã§");
    return;
  }

  pendingOCRRequestRef.current = true;

  // ÏÑ†ÌÉùÎêú IDÏóê Ìï¥ÎãπÌïòÎäî Ïù∏Îç±Ïä§ Ï∞æÍ∏∞
  const selectedIndex = rectangles.findIndex(rect => rect.id === selectedId);

  // Ïù∏Îç±Ïä§Î•º Ï∞æÏïòÏùÑ ÎïåÎßå ÏöîÏ≤≠ Ï†ÑÏÜ°
  if (selectedIndex !== -1) {
    // OCR ÏöîÏ≤≠ Î≥¥ÎÇ¥Í∏∞
    Streamlit.setComponentValue({
      mode: mode,
      bboxes: rectangles.map((rect) => ({
        bbox: [rect.x, rect.y, rect.width, rect.height],
        label: rect.label,
      })),
      scale: scale,
      request_ocr: true,
      selected_box_index: selectedIndex  // ID ÎåÄÏã† Ïù∏Îç±Ïä§ Ï†ÑÏÜ°
    });
  } else {
    console.error("ÏÑ†ÌÉùÎêú IDÏóê Ìï¥ÎãπÌïòÎäî Î∞îÏö¥Îî© Î∞ïÏä§Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§:", selectedId);
  }

  // ÏöîÏ≤≠ ÌîåÎûòÍ∑∏ Ï¥àÍ∏∞Ìôî (ÏùºÏ†ï ÏãúÍ∞Ñ ÌõÑ)
  setTimeout(() => {
    pendingOCRRequestRef.current = false;
  }, 1000);
};

// Î©îÏãúÏßÄ Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà ÏàòÏ†ï
useEffect(() => {
  const onDataFromPython = (event: MessageEvent) => {
    if (event.data.type === 'streamlit:render') {
      console.log("Ï†ÑÏ≤¥ Îç∞Ïù¥ÌÑ∞:", event.data.args);
      console.log("Ïª¥Ìè¨ÎÑåÌä∏ Îç∞Ïù¥ÌÑ∞:", event.data.args.data);
      try {
        const data = event.data.args.data;

        // OCR Ï∂îÏ≤ú Í≤∞Í≥º Ï≤òÎ¶¨ Í∞úÏÑ†
        if (data && data.ocr_suggestions && Array.isArray(data.ocr_suggestions)) {
          console.log("DEBUG: OCR Ï∂îÏ≤ú Î™©Î°ù Î∞õÏùå:", data.ocr_suggestions);

          // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏Î•º ÏùºÍ¥ÑÏ†ÅÏúºÎ°ú Ï≤òÎ¶¨
          setSuggestedLabels(data.ocr_suggestions);
          setIsLoadingLabels(false);
          setShowSuggestions(true);

          // ÎîîÎ≤ÑÍπÖ: Ïã§Ï†úÎ°ú UIÏóê ÌëúÏãúÎêòÎäîÏßÄ ÌôïÏù∏
          setTimeout(() => {
            console.log("DEBUG: UI ÏÉÅÌÉú ÏµúÏ¢Ö ÌôïÏù∏", {
              suggestedLabels: data.ocr_suggestions,
              showSuggestions: true
            });
          }, 100);
        }
      } catch (error) {
        console.error("Ïò§Î•ò Î∞úÏÉù:", error);
      }
    }
  };

  window.addEventListener('message', onDataFromPython);
  return () => window.removeEventListener('message', onDataFromPython);
}, [setSuggestedLabels, setIsLoadingLabels, setShowSuggestions]); // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Ìï®ÏàòÎì§ÏùÑ ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥Ïóê Ï∂îÍ∞Ä

  // ÌòÑÏû¨ ÏÑ†ÌÉùÎêú Î∞ïÏä§Ïùò ÎùºÎ≤®Î°ú ÏûÖÎ†•ÎûÄ ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (selectedId) {
      const selectedRect = rectangles.find(rect => rect.id === selectedId);
      if (selectedRect) {
        setLabel(selectedRect.label);
      }
    }
  }, [selectedId, rectangles]);

  // Î™®ÎìúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ StreamlitÏúºÎ°ú Ï†ÑÏÜ°
  useEffect(() => {
    sendStateToStreamlit(mode, rectangles, scale);
  }, [mode, rectangles]);

  // Î™®Îìú Î≥ÄÍ≤ΩÏù¥ Ï†úÎåÄÎ°ú ÎêòÎäîÏßÄ ÌôïÏù∏
  useEffect(() => {
    console.log("ÌòÑÏû¨ Î™®Îìú:", mode);
  }, [mode]);

  // ÏÇ¨Ïö©Ïûê ÏûÖÎ†•ÏùÑ Î∞òÏòÅÌïòÎäî Ìï®Ïàò
  const handleLabelInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newLabel = event.target.value;
    setLabel(newLabel);
    if (selectedId !== null) {
      setRectangles((prev) =>
        prev.map((rect) =>
          rect.id === selectedId ? { ...rect, label: newLabel, stroke: "#39FF14" } : rect
      // color_map[newLabel] || "#FF0000" } : rect
        )
      );
    }
  };


  // Ï∫îÎ≤ÑÏä§ ÌÅ¨Í∏∞ Ï°∞Ï†ï
  useEffect(() => {
    // Ï¥àÍ∏∞ Ïä§ÏºÄÏùº ÏÑ§Ï†ï Î∞è ÌîÑÎ†àÏûÑ ÎÜíÏù¥ Ï°∞Ï†ïÏùÑ ÌïòÎÇòÏùò useEffectÎ°ú ÌÜµÌï©
    const initializeScale = () => {
      // ÏµúÏ¥à Î°úÎìú Ïãú Ï¥àÍ∏∞ Ïä§ÏºÄÏùº Í≥ÑÏÇ∞
      if (scale === 1.0 && image_size[0] > 0) {
        const scale_ratio = window.innerWidth * 0.8 / image_size[0];
        setScale(Math.min(scale_ratio, 1.0));
      }
    };

    const updateFrameHeight = () => {
      // ÌòÑÏû¨ Ïù¥ÎØ∏ÏßÄ ÌÅ¨Í∏∞ÏôÄ Ïä§ÏºÄÏùº Í∏∞Î∞òÏúºÎ°ú ÌîÑÎ†àÏûÑ ÎÜíÏù¥ Ï°∞Ï†ï
      if (image_size[1] > 0) {
        Streamlit.setFrameHeight(image_size[1] * scale + 100);
      }
    };

    // Ï¥àÍ∏∞Ìôî Î∞è Ï≤´ Î≤àÏß∏ ÌîÑÎ†àÏûÑ ÎÜíÏù¥ ÏÑ§Ï†ï
    initializeScale();
    updateFrameHeight();

    // ÏúàÎèÑÏö∞ Î¶¨ÏÇ¨Ïù¥Ï¶à Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    window.addEventListener('resize', updateFrameHeight);
    return () => {
      window.removeEventListener('resize', updateFrameHeight);
    };
  }, [image_size, scale]); // Ïù¥ÎØ∏ÏßÄ ÌÅ¨Í∏∞ÏôÄ Ïä§ÏºÄÏùº ÏùòÏ°¥ÏÑ± Ï∂îÍ∞Ä


  // ÌÇ§Î≥¥Îìú Îã®Ï∂ïÌÇ§ Ï≤òÎ¶¨
  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      console.log("keydown Ïù¥Î≤§Ìä∏ Í∞êÏßÄÎê®:", event.code, "Ctrl:", event.ctrlKey, "Shift:", event.shiftKey);

      if (use_space && event.code === "Space") {
        sendStateToStreamlit(mode, rectangles, scale);  
      }

      if (event.ctrlKey && event.code === "KeyE") {
        event.preventDefault();
        console.log("Î≥ÄÍ≤Ω: Edit Î™®ÎìúÎ°ú Ï†ÑÌôò");
        setMode("Edit");
      }

      if (event.ctrlKey && event.code === "KeyD") {
        event.preventDefault();
        console.log("Î≥ÄÍ≤Ω: Draw Î™®ÎìúÎ°ú Ï†ÑÌôò");
        setMode("Draw");
      }

      // ÎùºÎ≤® ÏûÖÎ†• Î™®Îìú ÌÜ†Í∏Ä (Ctrl + L)
      if (event.ctrlKey && event.code === "KeyL") {
        event.preventDefault();
        console.log("Ctrl+L Í∞êÏßÄ, ÏÑ†ÌÉùÎêú ID:", selectedId);

        if (selectedId) {
          // ÏÉÅÌÉú Î≥ÄÍ≤ΩÏùÑ ÏùºÍ¥Ñ Ï≤òÎ¶¨ÌïòÍ≥† Ìï®ÏàòÌòï ÏóÖÎç∞Ïù¥Ìä∏ ÏÇ¨Ïö©
          setIsLabelEditMode(true);
          setShowSuggestions(true);

          // OCR ÏöîÏ≤≠ÏùÑ Îã§Ïùå Î†åÎçîÎßÅ ÏÇ¨Ïù¥ÌÅ¥Î°ú ÏßÄÏó∞
          // Î†åÎçîÎßÅ ÏÇ¨Ïù¥ÌÅ¥Ïù¥ ÏôÑÎ£åÎêú ÌõÑ Ïã§ÌñâÎêòÎèÑÎ°ù Ìï®
          requestAnimationFrame(() => {
            setIsLoadingLabels(true);
            sendOCRRequest(selectedId, mode, rectangles, scale);
          });
        }
      }

      // ÎùºÎ≤® ÌëúÏãú ÌÜ†Í∏Ä (Ctrl + T)
      if (event.ctrlKey && event.code === "KeyT") {
        event.preventDefault();
        console.log("Î≥ÄÍ≤Ω: ÎùºÎ≤® ÌëúÏãú ÌÜ†Í∏Ä");
        setShowLabels(prev => !prev);
      }

      // Ctrl+S Îã®Ï∂ïÌÇ§ Í∞êÏßÄ Ï∂îÍ∞Ä
      if (event.ctrlKey && event.code === "KeyS") {
        event.preventDefault();
        console.log("Ctrl+S Í∞êÏßÄ: Ïñ¥ÎÖ∏ÌÖåÏù¥ÏÖò Ï†ÄÏû•");

        // ÏïàÎÇ¥ ÌëúÏãú ÌôúÏÑ±Ìôî
        setSaveNotification(true);

        // 3Ï¥à ÌõÑÏóê ÏïàÎÇ¥ Î©îÏãúÏßÄ Ïà®Í∏∞Í∏∞
        setTimeout(() => {
          setSaveNotification(false);
        }, 3000);

        // StreamlitÏúºÎ°ú Ï†ÄÏû• Î™ÖÎ†π Ï†ÑÏÜ°
        Streamlit.setComponentValue({
          mode: mode,
          bboxes: rectangles.map((rect) => ({
            bbox: [rect.x, rect.y, rect.width, rect.height],
            label: rect.label,
          })),
          scale: scale,
          save_requested: true  // Ï†ÄÏû• ÏöîÏ≤≠ ÌîåÎûòÍ∑∏ Ï∂îÍ∞Ä
        });
      }
      // ÏÇ≠Ï†ú Í∏∞Îä•
      if (event.code === "Delete" && selectedId) {
        event.preventDefault();
        console.log("ÏÇ≠Ï†ú: ÏÑ†ÌÉùÎêú Î∞îÏö¥Îî© Î∞ïÏä§ ÏÇ≠Ï†ú", selectedId);
        setRectangles((prev) => prev.filter((rect) => rect.id !== selectedId));
        setSelectedId(null);
        setIsLabelEditMode(false);
      }

      // ESC ÌÇ§Î°ú ÎùºÎ≤® ÏûÖÎ†• Î™®Îìú Ï∑®ÏÜå Î∞è ÏÑ†ÌÉù Ìï¥Ï†ú
      if (event.code === "Escape") {
        event.preventDefault();
        if (isLabelEditMode) {
          setIsLabelEditMode(false);
        } else if (selectedId) {
          setSelectedId(null);
        }
      }
    };

    window.addEventListener("keydown", handleKeyPress);
    return () => {
      window.removeEventListener("keydown", handleKeyPress);
    };
  }, [rectangles, use_space, selectedId, isLabelEditMode, mode]);

  // ÎßàÏö∞Ïä§ Ìú† ÌôïÎåÄ/Ï∂ïÏÜå
  useEffect(() => {
    const handleWheel = (event: WheelEvent) => {
      if (event.ctrlKey) {
        event.preventDefault();

        let newScale = scale + (event.deltaY < 0 ? 0.1 : -0.1);
        newScale = Math.min(Math.max(newScale, 0.5), 3.0);
        setScale(newScale);
      }
    };

    window.addEventListener('wheel', handleWheel, { passive: false, capture: true });
  }, [scale]);

  // Ïä§ÏºÄÏùº Ìè¨Îß∑ÌåÖ
  const formatScale = (scale: number) => {
    return `${Math.round(scale * 100)}%`;
  };

  // ÎùºÏù¥Ìä∏ Î™®ÎìúÎ°ú Í≥†Ï†ïÎêú ÏÉâÏÉÅ ÏÑ§Ï†ï
  const borderColor = 'gray.200';
  const textColor = 'black';
  const controlBgColor = 'gray.200';
  const inputbgColor = 'white';

  const handleModeChange = (newMode: string) => {
    setMode(newMode);
    // scaleÏùÑ Ïú†ÏßÄÌïòÎ©¥ÏÑú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
    sendStateToStreamlit(newMode, rectangles, scale);
  };

  // ÎùºÎ≤® Ï∂îÏ≤ú ÏÑ†ÌÉù Ìï∏Îì§Îü¨ Ìï®Ïàò Ï∂îÍ∞Ä
  const handleSuggestionSelect = (suggestedLabel: string) => {
    setLabel(suggestedLabel);
    if (selectedId !== null) {
      setRectangles((prev) =>
        prev.map((rect) =>
          rect.id === selectedId ? { ...rect, label: suggestedLabel, stroke: "#39FF14" } : rect
        )
      );
    }
    setShowSuggestions(false); // ÏÑ†ÌÉù ÌõÑ Ï∂îÏ≤ú UI Îã´Í∏∞
  };


  return (
    <ChakraProvider theme={theme}>
      <VStack spacing={4} align="stretch">
        {/* Ï†ÄÏû• ÏïåÎ¶º Î©îÏãúÏßÄ */}
        {saveNotification && (
          <Box 
            p={3} 
            bg="green.100" 
            color="green.800" 
            borderRadius="md" 
            position="fixed" 
            top="20px" 
            left="50%" 
            transform="translateX(-50%)" 
            zIndex={100}
            boxShadow="md"
            display="flex"
            alignItems="center"
            justifyContent="center"
          >
            <Text fontWeight="bold">
              <span role="img" aria-label="save">üíæ</span> Ïñ¥ÎÖ∏ÌÖåÏù¥ÏÖò Ï†ÄÏû• ÏöîÏ≤≠Ïù¥ Ï†ÑÏÜ°ÎêòÏóàÏäµÎãàÎã§!
            </Text>
          </Box>
        )}

        {/* ÏÉÅÎã® Ïª®Ìä∏Î°§ ÏòÅÏó≠ */}
        <Box 
          p={3} 
          bg={controlBgColor} 
          borderRadius="md" 
          position="sticky" 
          top={0} 
          zIndex={10}
          borderColor={borderColor}
          borderWidth="1px"
          boxShadow="sm"
        >
          <Flex justifyContent="space-between" alignItems="center">
            <HStack spacing={4}>
              {/* ÎùºÎ≤® ÏûÖÎ†• */}
              <Box>
                <Text fontSize="sm" mb={1} color={textColor}>Class</Text>
                <Input 
                  value={label} 
                  onChange={handleLabelInputChange} 
                  placeholder="Enter label"
                  size="sm"
                  width="200px"
                  bg={inputbgColor}
                  borderColor={borderColor}
                />
              </Box>
            </HStack>

            <HStack>
              <Text fontSize="sm" mr={2} color={textColor}>Zoom: {formatScale(scale)}</Text>
              <Button 
                size="sm" 
                colorScheme="teal"
                variant={showLabels ? "solid" : "outline"}
                opacity={showLabels ? 1 : 0.7}
                onClick={() => setShowLabels(prev => !prev)}
              >
                {showLabels ? "Hide Labels" : "Show Labels"}
              </Button>

              <Button 
                size="sm" 
                colorScheme="green"
                variant={mode === "Draw" ? "solid" : "outline"}
                opacity={mode === "Draw" ? 1 : 0.7}
                // onClick={() => setMode("Draw")}
                onClick={() => handleModeChange("Draw")} // ÏßÅÏ†ë setMode ÎåÄÏã† Ìï∏Îì§Îü¨ ÏÇ¨Ïö©
              >
                Draw
              </Button>
              <Button 
                size="sm" 
                colorScheme="blue"
                variant={mode === "Edit" ? "solid" : "outline"}
                opacity={mode === "Edit" ? 1 : 0.7}
                onClick={() => setMode("Edit")}
              >
                Edit
              </Button>
              <Button 
                size="sm" 
                colorScheme="purple"
                onClick={() => {
                  // Ï†ÄÏû• ÏöîÏ≤≠ ÌîåÎûòÍ∑∏Î•º Ìè¨Ìï®ÌïòÏó¨ StreamlitÏúºÎ°ú Îç∞Ïù¥ÌÑ∞ Ï†ÑÏÜ°
                  setSaveNotification(true);
                  setTimeout(() => setSaveNotification(false), 3000);

                  Streamlit.setComponentValue({
                    mode: mode,
                    bboxes: rectangles.map((rect) => ({
                      bbox: [rect.x, rect.y, rect.width, rect.height],
                      label: rect.label,
                    })),
                    scale: scale,
                    save_requested: true
                  });
                }}
              >
                Save
              </Button>
            </HStack>
          </Flex>

          <Flex mt={2} justifyContent="space-between" alignItems="center">
            <Text fontSize="xs" color={textColor}>Selected Items: {rectangles.length}</Text>
          </Flex>
        </Box>

        {/* Ïù¥ÎØ∏ÏßÄ ÏòÅÏó≠ */}
        <Box>
          <Center>
          <BBoxCanvas
            rectangles={rectangles}
            mode={mode}
            selectedId={selectedId}
            scale={scale}
            setSelectedId={setSelectedId}
            setRectangles={setRectangles}
            setLabel={setLabel}
            color_map={color_map}
            label={label}
            image={image}
            image_size={image_size}
            strokeWidth={line_width}
            isLabelEditMode={isLabelEditMode}
            setIsLabelEditMode={setIsLabelEditMode}
            handleLabelInputChange={handleLabelInputChange}
            showLabels={showLabels}
            suggestedLabels={suggestedLabels} // Ïó¨Í∏∞Í∞Ä Ï†úÎåÄÎ°ú Ï†ÑÎã¨ÎêòÎäîÏßÄ ÌôïÏù∏
            showSuggestions={showSuggestions} // Ïó¨Í∏∞Í∞Ä trueÎ°ú ÏÑ§Ï†ïÎêòÎäîÏßÄ ÌôïÏù∏
            setShowSuggestions={setShowSuggestions}
            handleSuggestionSelect={handleSuggestionSelect}
            isLoadingLabels={isLoadingLabels}
          />
          </Center>
        </Box>
      </VStack>
    </ChakraProvider>
  );
};

export default withStreamlitConnection(Detection);
